parameters.responseBody:
={{ {
  "status": "success",
  "message": "Data pipeline completed successfully",
  "reportSummary": {
    "executionId": $json.executionId,
    "version": $json.version,
    "totalRecords": $json.summary.totalRecords,
    "dataQuality": $json.summary.dataQuality,
    "processingTime": $json.summary.processingDuration + "ms",
    "enrichmentApplied": $json.summary.enrichmentApplied
  },
  "outputs": {
    "database": "PostgreSQL - audit_logs, processed_data, data_versions updated",
    "metrics": "Workflow metrics updated",
    "report": "HTML report generated (simulated)"
  },
  "timestamp": new Date().toISOString(),
  "pipelineComplete": true
} }}
