// Data Collection & Initial Processing
const timestamp = new Date().toISOString();
const executionId = `exec_${Date.now()}`;
const version = `v${new Date().getFullYear()}.${(new Date().getMonth() + 1).toString().padStart(2, '0')}.${Date.now()}`;

const allData = [];
const collectionMetrics = {
  total_sources: 0,
  successful_collections: 0,
  failed_collections: 0,
  collection_timestamp: timestamp
};

// Process all input data
for (const item of $input.all()) {
  const data = item.json;
  const nodeInfo = item.pairedItem;

  collectionMetrics.total_sources++;

  let processedItem = {
    execution_id: executionId,
    version: version,
    collected_at: timestamp,
    source: 'unknown',
    data_type: 'unknown',
    raw_data: data,
    collection_status: 'success'
  };

  // Identify and process weather data
  if (data.main && data.coord && data.weather) {
    processedItem.source = 'openweather';
    processedItem.data_type = 'weather';
    processedItem.location = {
      city: data.name,
      country: data.sys?.country,
      lat: data.coord.lat,
      lon: data.coord.lon
    };
    processedItem.metrics = {
      temperature: data.main.temp,
      humidity: data.main.humidity,
      pressure: data.main.pressure,
      description: data.weather[0]?.description,
      wind_speed: data.wind?.speed,
      visibility: data.visibility
    };
    collectionMetrics.successful_collections++;
  }

  // Identify and process news data
  else if (data.articles && Array.isArray(data.articles)) {
    processedItem.source = 'newsapi';
    processedItem.data_type = 'news';
    processedItem.metrics = {
      total_articles: data.totalResults,
      articles_fetched: data.articles.length,
      latest_article_time: data.articles[0]?.publishedAt
    };
    processedItem.articles = data.articles.slice(0, 5).map(article => ({
      title: article.title,
      description: article.description,
      url: article.url,
      published_at: article.publishedAt,
      source: article.source?.name
    }));
    collectionMetrics.successful_collections++;
  }

  // Identify and process crypto data
  else if (data.bpi) {
    processedItem.source = 'coindesk';
    processedItem.data_type = 'crypto';
    processedItem.metrics = {
      usd_rate: parseFloat(data.bpi.USD.rate.replace(/,/g, '')),
      eur_rate: parseFloat(data.bpi.EUR.rate.replace(/,/g, '')),
      gbp_rate: parseFloat(data.bpi.GBP.rate.replace(/,/g, '')),
      last_updated: data.time.updated
    };
    collectionMetrics.successful_collections++;
  }

  // Handle errors or unknown data
  else if (data.error || data.message) {
    processedItem.collection_status = 'failed';
    processedItem.error_message = data.message || data.error;
    collectionMetrics.failed_collections++;
  }

  allData.push(processedItem);
}

// Add collection summary
const summary = {
  execution_id: executionId,
  version: version,
  timestamp: timestamp,
  collection_metrics: collectionMetrics,
  total_items: allData.length,
  stage: 'collection_complete'
};

allData.push(summary);

return allData.map(item => ({ json: item }));

// Advanced Data Validation with Quality Scoring
const items = $input.all();
const validatedItems = [];
const validationErrors = [];
const validationSummary = {
  total_items: 0,
  valid_items: 0,
  invalid_items: 0,
  average_quality_score: 0
};

let totalQualityScore = 0;

for (const item of items) {
  const data = item.json;

  // Skip summary items
  if (data.stage === 'collection_complete') {
    validatedItems.push({ json: data });
    continue;
  }

  validationSummary.total_items++;

  // Validation rules and quality scoring
  let qualityScore = 100;
  let isValid = true;
  let validationIssues = [];

  // Basic validation
  if (!data.execution_id) {
    isValid = false;
    qualityScore -= 30;
    validationIssues.push('Missing execution ID');
  }

  if (!data.collected_at) {
    isValid = false;
    qualityScore -= 20;
    validationIssues.push('Missing timestamp');
  }

  if (!data.source || data.source === 'unknown') {
    qualityScore -= 15;
    validationIssues.push('Unknown data source');
  }

  // Data-specific validation
  if (data.data_type === 'weather') {
    if (!data.metrics?.temperature) {
      qualityScore -= 25;
      validationIssues.push('Missing temperature data');
    }
    if (data.metrics?.temperature && (data.metrics.temperature < -50 || data.metrics.temperature > 50)) {
      qualityScore -= 20;
      validationIssues.push('Temperature out of range');
    }
    if (!data.metrics?.humidity || data.metrics.humidity < 0 || data.metrics.humidity > 100) {
      qualityScore -= 15;
      validationIssues.push('Invalid humidity data');
    }
  }

  if (data.data_type === 'news') {
    if (!data.articles || data.articles.length === 0) {
      qualityScore -= 30;
      validationIssues.push('No articles found');
    }
    if (data.articles) {
      const invalidArticles = data.articles.filter(article => !article.title || !article.url);
      if (invalidArticles.length > 0) {
        qualityScore -= (invalidArticles.length * 10);
        validationIssues.push(`${invalidArticles.length} articles missing required fields`);
      }
    }
  }

  if (data.data_type === 'crypto') {
    if (!data.metrics?.usd_rate || data.metrics.usd_rate <= 0) {
      qualityScore -= 25;
      validationIssues.push('Invalid USD rate');
    }
  }

  // Apply minimum quality threshold
  if (qualityScore < 50) {
    isValid = false;
  }

  // Create validated item
  const validatedItem = {
    ...data,
    validation: {
      is_valid: isValid,
      quality_score: Math.max(0, qualityScore),
      issues: validationIssues,
      validated_at: new Date().toISOString(),
      validation_rules_version: 'v1.0'
    }
  };

  if (isValid) {
    validationSummary.valid_items++;
    totalQualityScore += qualityScore;
    validatedItems.push({ json: validatedItem });
  } else {
    validationSummary.invalid_items++;
    validationErrors.push({
      json: {
        ...validatedItem,
        error_type: 'validation_failed',
        severity: qualityScore < 30 ? 'critical' : 'warning'
      }
    });
  }
}

// Calculate average quality score
if (validationSummary.valid_items > 0) {
  validationSummary.average_quality_score = Math.round(totalQualityScore / validationSummary.valid_items);
}

// Add validation summary
validatedItems.push({
  json: {
    stage: 'validation_complete',
    validation_summary: validationSummary,
    timestamp: new Date().toISOString()
  }
});

// Store validation errors for alerting
if (validationErrors.length > 0) {
  $node['Alert on Validation Error'].emit(validationErrors);
}

return validatedItems;

// ML-Enhanced Data Processing & Enrichment
const items = $input.all();
const processedItems = [];
const processingMetrics = {
  items_processed: 0,
  enrichment_features_added: 0,
  ml_features_extracted: 0,
  anomalies_detected: 0
};

for (const item of items) {
  const data = item.json;

  // Skip non-data items
  if (data.stage === 'collection_complete' || data.stage === 'validation_complete') {
    processedItems.push({ json: data });
    continue;
  }

  if (!data.validation?.is_valid) {
    continue; // Skip invalid items
  }

  processingMetrics.items_processed++;

  // Base processed item
  let processedItem = {
    ...data,
    processing: {
      processed_at: new Date().toISOString(),
      processing_version: 'v2.0',
      features_extracted: []
    },
    enrichment: {},
    ml_features: {},
    anomaly_detection: {
      is_anomaly: false,
      anomaly_score: 0,
      anomaly_reasons: []
    }
  };

  // Weather data processing
  if (data.data_type === 'weather') {
    // Enrichment
    processedItem.enrichment = {
      heat_index: calculateHeatIndex(data.metrics.temperature, data.metrics.humidity),
      comfort_level: getComfortLevel(data.metrics.temperature, data.metrics.humidity),
      season: getSeason(),
      time_of_day: getTimeOfDay(),
      weather_category: categorizeWeather(data.metrics.description)
    };

    // ML Features
    processedItem.ml_features = {
      temperature_normalized: normalizeTemperature(data.metrics.temperature),
      humidity_normalized: data.metrics.humidity / 100,
      pressure_normalized: normalizePressure(data.metrics.pressure),
      temperature_anomaly_score: detectTemperatureAnomaly(data.metrics.temperature)
    };

    // Anomaly detection
    if (data.metrics.temperature < -20 || data.metrics.temperature > 45) {
      processedItem.anomaly_detection.is_anomaly = true;
      processedItem.anomaly_detection.anomaly_score += 0.8;
      processedItem.anomaly_detection.anomaly_reasons.push('Extreme temperature');
      processingMetrics.anomalies_detected++;
    }

    processingMetrics.enrichment_features_added += 5;
    processingMetrics.ml_features_extracted += 4;
  }

  // News data processing
  if (data.data_type === 'news') {
    // Enrichment
    processedItem.enrichment = {
      article_sentiment: analyzeArticleSentiment(data.articles),
      trending_topics: extractTrendingTopics(data.articles),
      average_article_age_hours: calculateAverageArticleAge(data.articles),
      source_diversity: calculateSourceDiversity(data.articles)
    };

    // ML Features
    processedItem.ml_features = {
      title_length_avg: calculateAverageTitleLength(data.articles),
      description_completeness: calculateDescriptionCompleteness(data.articles),
      freshness_score: calculateFreshnessScore(data.articles),
      content_quality_score: calculateContentQuality(data.articles)
    };

    // Anomaly detection
    if (data.articles.length < 2) {
      processedItem.anomaly_detection.is_anomaly = true;
      processedItem.anomaly_detection.anomaly_score += 0.6;
      processedItem.anomaly_detection.anomaly_reasons.push('Low article count');
      processingMetrics.anomalies_detected++;
    }

    processingMetrics.enrichment_features_added += 4;
    processingMetrics.ml_features_extracted += 4;
  }

  // Crypto data processing
  if (data.data_type === 'crypto') {
    // Enrichment
    processedItem.enrichment = {
      price_trend: analyzePriceTrend(data.metrics.usd_rate),
      volatility_indicator: 'normal', // Would need historical data for real calculation
      market_sentiment: 'neutral',
      price_category: categorizeBitcoinPrice(data.metrics.usd_rate)
    };

    // ML Features
    processedItem.ml_features = {
      usd_rate_normalized: normalizePrice(data.metrics.usd_rate, 20000, 80000),
      currency_spread: calculateCurrencySpread(data.metrics),
      price_stability_score: 0.8 // Placeholder
    };

    // Anomaly detection
    if (data.metrics.usd_rate < 15000 || data.metrics.usd_rate > 100000) {
      processedItem.anomaly_detection.is_anomaly = true;
      processedItem.anomaly_detection.anomaly_score += 0.7;
      processedItem.anomaly_detection.anomaly_reasons.push('Extreme price movement');
      processingMetrics.anomalies_detected++;
    }

    processingMetrics.enrichment_features_added += 4;
    processingMetrics.ml_features_extracted += 3;
  }

  // Add processing metadata
  processedItem.processing.features_extracted = Object.keys(processedItem.enrichment).concat(Object.keys(processedItem.ml_features));
  processedItem.processing.processing_duration_ms = Math.random() * 500 + 100; // Simulated

  processedItems.push({ json: processedItem });
}

// Add processing summary
processedItems.push({
  json: {
    stage: 'processing_complete',
    processing_metrics: processingMetrics,
    timestamp: new Date().toISOString()
  }
});

// Helper functions
function calculateHeatIndex(temp, humidity) {
  return temp + (humidity / 100) * (temp - 14.4);
}

function getComfortLevel(temp, humidity) {
  if (temp >= 20 && temp <= 26 && humidity >= 40 && humidity <= 60) return 'comfortable';
  if (temp < 16 || temp > 30) return 'uncomfortable';
  return 'moderate';
}

function getSeason() {
  const month = new Date().getMonth();
  if (month >= 2 && month <= 4) return 'spring';
  if (month >= 5 && month <= 7) return 'summer';
  if (month >= 8 && month <= 10) return 'fall';
  return 'winter';
}

function getTimeOfDay() {
  const hour = new Date().getHours();
  if (hour >= 6 && hour < 12) return 'morning';
  if (hour >= 12 && hour < 18) return 'afternoon';
  if (hour >= 18 && hour < 22) return 'evening';
  return 'night';
}

function categorizeWeather(description) {
  if (!description) return 'unknown';
  const desc = description.toLowerCase();
  if (desc.includes('clear') || desc.includes('sunny')) return 'clear';
  if (desc.includes('cloud')) return 'cloudy';
  if (desc.includes('rain')) return 'rainy';
  if (desc.includes('snow')) return 'snowy';
  return 'other';
}

function normalizeTemperature(temp) {
  return (temp + 50) / 100; // Normalize to 0-1 range assuming -50 to 50 range
}

function normalizePressure(pressure) {
  return (pressure - 950) / (1050 - 950); // Normalize to 0-1 range
}

function detectTemperatureAnomaly(temp) {
  const avgTemp = 15; // Global average
  const deviation = Math.abs(temp - avgTemp);
  return Math.min(deviation / 30, 1); // Normalize to 0-1
}

function analyzeArticleSentiment(articles) {
  // Simplified sentiment analysis
  return 'neutral'; // Would use NLP library in real implementation
}

function extractTrendingTopics(articles) {
  const words = articles.flatMap(a => (a.title || '').toLowerCase().split(' '))
    .filter(word => word.length > 4)
    .reduce((acc, word) => {
      acc[word] = (acc[word] || 0) + 1;
      return acc;
    }, {});

  return Object.entries(words)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 5)
    .map(([word]) => word);
}

function calculateAverageArticleAge(articles) {
  const now = new Date();
  const ages = articles.map(article => {
    const published = new Date(article.published_at);
    return (now - published) / (1000 * 60 * 60); // Hours
  });
  return ages.reduce((sum, age) => sum + age, 0) / ages.length;
}

function calculateSourceDiversity(articles) {
  const sources = new Set(articles.map(a => a.source));
  return sources.size / articles.length;
}

function calculateAverageTitleLength(articles) {
  return articles.reduce((sum, a) => sum + (a.title || '').length, 0) / articles.length;
}

function calculateDescriptionCompleteness(articles) {
  const withDesc = articles.filter(a => a.description && a.description.length > 50);
  return withDesc.length / articles.length;
}

function calculateFreshnessScore(articles) {
  const avgAge = calculateAverageArticleAge(articles);
  return Math.max(0, 1 - (avgAge / 24)); // Fresh if less than 24 hours
}

function calculateContentQuality(articles) {
  return articles.reduce((sum, a) => {
    let score = 0.5;
    if (a.title && a.title.length > 20) score += 0.2;
    if (a.description && a.description.length > 100) score += 0.2;
    if (a.url && a.url.startsWith('https://')) score += 0.1;
    return sum + score;
  }, 0) / articles.length;
}

function analyzePriceTrend(currentPrice) {
