function categorizeBitcoinPrice(price) {
  if (price < 30000) return 'low';
  if (price < 60000) return 'medium';
  return 'high';
}

function normalizePrice(price, min, max) {
  return Math.max(0, Math.min(1, (price - min) / (max - min)));
}

function calculateCurrencySpread(metrics) {
  const rates = [metrics.usd_rate, metrics.eur_rate, metrics.gbp_rate];
  const max = Math.max(...rates);
  const min = Math.min(...rates);
  return (max - min) / max;
}

