function generateRecommendations(stats, dataItems) {
  const recommendations = [];

  if (stats.average_quality_score < 80) {
    recommendations.push({
      type: 'data_quality',
      priority: 'high',
      message: 'Data quality score is below 80%. Review data validation rules and source reliability.'
    });
  }

  if (stats.anomalies_detected > 0) {
    recommendations.push({
      type: 'anomaly_investigation',
      priority: 'medium',
      message: `${stats.anomalies_detected} anomalies detected. Investigate for potential data source issues.`
    });
  }

  if (stats.total_items < 3) {
    recommendations.push({
      type: 'data_coverage',
      priority: 'low',
      message: 'Limited data sources active. Consider adding more data sources for comprehensive analysis.'
    });
  }

  return recommendations;
}

